    def btConfermaClicked(self):
        # Ottieni il numero del tavolo dalla QLineEdit e rimuovi eventuali spazi all'inizio o alla fine
        numero_tavolo = self.lineEdit.text().strip()

        # Verifica se il numero del tavolo Ã¨ vuoto dopo aver rimosso gli spazi
        if not numero_tavolo:
            QtWidgets.QMessageBox.warning(None, "Errore", "Inserisci un numero per il tavolo.")
            return

        # Ottieni la sala selezionata dalla QComboBox
        sala = self.comboBox.currentText().lower()  # Converte la sala in minuscolo

        # Stato predefinito del tavolo
        stato = "libero"

        # Ora puoi aggiungere questi dati al database
        try:
            myconn = get_connection()
            cursor = myconn.cursor()

            # Esecuzione della query per inserire il nuovo tavolo nel database
            query = "INSERT INTO tavolo (numero, sala, stato) VALUES (%s, %s, %s)"
            cursor.execute(query, (numero_tavolo, sala, stato))

            # Conferma l'esecuzione della query
            myconn.commit()

            # Mostra un messaggio di successo
            QtWidgets.QMessageBox.information(None, "Successo", "Tavolo aggiunto con successo al database.")

        except Error as e:
            print(f"Errore durante l'accesso al database: {e}")

            # Mostra un messaggio di errore
            QtWidgets.QMessageBox.critical(None, "Errore", f"Errore durante l'aggiunta del tavolo al database: {e}")

        finally:
            if myconn:
                myconn.close()
            self.form.close()


