    def btEliminaNomeClicked(self):
        selectedRow = self.tableNomi.currentRow()
        if selectedRow == -1:
            dlg = QMessageBox()
            dlg.setWindowTitle("Errore")
            dlg.setText("Non hai selezionato nessun elemento!")
            dlg.setStandardButtons(QMessageBox.Ok)
            dlg.setIcon(QMessageBox.Critical)
            dlg.exec()
        else:
            dlg = QMessageBox()
            dlg.setWindowTitle("Eliminazione elemento magazzino")
            dlg.setText(
                f"Sicuro di voler eliminare il nomeElemento con nome: {self.tableNomi.item(selectedRow, 2).text()}>")
            dlg.setStandardButtons(QMessageBox.Yes | QMessageBox.No)
            dlg.setIcon(QMessageBox.Warning)
            button = dlg.exec()
            if button == QMessageBox.Yes:
                query = f"DELETE FROM nomeelemento WHERE id = {self.tableNomi.item(selectedRow, 0).text()}"
                GestoreDBMagazzino.eseguiQuery()
            self.load_data_tabella()

    def load_data_tabella(self):
        result = GestoreDBMagazzino.ottieniNomiElementiGestioneNomi()
        # self.db.reconnect()
        # cur = self.db.cursor()
        # query = """select *
        #         from nomeelemento"""
        # cur.execute(query)
        # result = cur.fetchall()
        self.tableNomi.setRowCount(0)
        for row_number, row_data in enumerate(result):
            print(row_number)
            self.tableNomi.insertRow(row_number)
            for column_number, data in enumerate(row_data):
                print(f"{data}, {column_number}")
                if column_number == 2 or column_number == 3:
                    if data == 0:
                        self.tableNomi.setItem(row_number, column_number, QTableWidgetItem("No"))
                    elif data == 1:
                        self.tableNomi.setItem(row_number, column_number, QTableWidgetItem("Si"))
                else:
                    self.tableNomi.setItem(row_number, column_number, QTableWidgetItem(str(data)))

        self.tableNomi.resizeColumnsToContents()




